pragma solidity >=0.8.0 <=0.8.4;

import "@teleportdao/btc-evm-bridge/contracts/types/ScriptTypesEnum.sol";

interface ILockersManager {
    /// @notice Structure for registering lockers
    /// @param lockerLockingScript Locker redeem script
    /// @param lockerScriptType Locker script type
    /// @param lockerRescueScript Locker script in case of getting BTCs back
    /// @param TSTLockedAmount Amount of TST locked by Locker
    /// @param collateralTokenLockedAmount Amount of collateral token locked by Locker
    /// @param netMinted Total minted - total burnt
    /// @param slashingTeleBTCAmount Total amount of teleBTC a locker must be slashed
    /// @param reservedCollateralTokenForSlash Total collateral token reserved for slashing locker
    /// @param isLocker True if it's a Locker
    /// @param isCandidate True if it's a candidate
    /// @param isScriptHash NOT USED
    struct locker {
        bytes lockerLockingScript;
        ScriptTypes lockerScriptType;
        bytes lockerRescueScript;
        uint256 TSTLockedAmount;
        uint256 collateralTokenLockedAmount;
        uint256 netMinted;
        uint256 slashingTeleBTCAmount;
        uint256 reservedCollateralTokenForSlash;
        bool isLocker;
        bool isCandidate;
        bool isScriptHash;
    }

    struct lockersLibConstants {
        uint256 OneHundredPercent;
        uint256 HealthFactor;
        uint256 UpperHealthFactor;
        uint256 MaxLockerFee;
        uint256 NativeTokenDecimal;
        address NativeToken;
    }

    struct lockersLibParam {
        address TeleportSystemToken;
        address teleBTC;
        address burnRouter;
        address exchangeConnector;
        address priceOracle;
        uint256 minRequiredTSTLockedAmount;
        uint256 minRequiredTNTLockedAmount;
        uint256 lockerPercentageFee;
        uint256 collateralRatio;
        uint256 liquidationRatio;
        uint256 priceWithDiscountRatio;
        uint256 inactivationDelay;
    }

    struct becomeLockerArguments {
        ILockersManager.lockersLibConstants libConstants;
        ILockersManager.lockersLibParam libParams;
        address theLockerTargetAddress;
        address collateralToken;
        uint256 collateralDecimal;
        uint256 _lockedTSTAmount;
        uint256 _lockedCollateralTokenAmount;
        bytes _candidateLockingScript;
        ScriptTypes _lockerScriptType;
        bytes _lockerRescueScript;
    }

    // Events

    event RequestAddLocker(
        address indexed lockerTargetAddress,
        bytes lockerLockingScript,
        uint TSTLockedAmount,
        address indexed collateralToken,
        uint collateralTokenLockedAmount
    );

    event RevokeAddLockerRequest(
        address indexed lockerTargetAddress,
        bytes lockerLockingScript,
        uint TSTLockedAmount,
        address indexed collateralToken,
        uint collateralTokenLockedAmount
    );

    event RequestInactivateLocker(
        address indexed lockerTargetAddress,
        uint256 indexed inactivationTimestamp,
        bytes lockerLockingScript,
        uint TSTLockedAmount,
        address collateralToken,
        uint collateralTokenLockedAmount,
        uint netMinted
    );

    event ActivateLocker(
        address indexed lockerTargetAddress,
        bytes lockerLockingScript,
        uint TSTLockedAmount,
        address collateralToken,
        uint collateralTokenLockedAmount,
        uint netMinted
    );

    event LockerAdded(
        address indexed lockerTargetAddress,
        bytes lockerLockingScript,
        uint TSTLockedAmount,
        address indexed collateralToken,
        uint collateralTokenLockedAmount,
        uint reliabilityFactor,
        uint addingTime
    );

    event LockerRemoved(
        address indexed lockerTargetAddress,
        bytes lockerLockingScript,
        uint TSTUnlockedAmount,
        address indexed collateralToken,
        uint collateralTokenUnlockedAmount
    );

    event LockerSlashed(
        address indexed lockerTargetAddress,
        address collateralToken,
        uint rewardAmount,
        address indexed rewardRecipient,
        uint256 amount,
        address indexed recipient,
        uint256 slashedCollateralAmount,
        uint256 slashTime,
        bool isForCCBurn
    );

    event LockerLiquidated(
        address indexed lockerTargetAddress,
        address indexed liquidatorAddress,
        address collateralToken,
        uint collateralAmount,
        uint teleBTCAmount,
        uint liquidateTime
    );

    event LockerSlashedCollateralSold(
        address indexed lockerTargetAddress,
        address indexed buyerAddress,
        address indexed collateralToken,
        uint slashingAmount,
        uint teleBTCAmount,
        uint slashingTime
    );

    event CollateralAdded(
        address indexed lockerTargetAddress,
        address indexed collateralToken,
        uint addedCollateral,
        uint totalCollateral,
        uint addingTime
    );

    event CollateralRemoved(
        address indexed lockerTargetAddress,
        address indexed collateralToken,
        uint removedCollateral,
        uint totalCollateral,
        uint removingTime
    );

    event MintByLocker(
        address indexed lockerTargetAddress,
        address indexed receiver,
        uint256 mintedAmount,
        uint256 lockerFee,
        uint256 mintingTime
    );

    event BurnByLocker(
        address indexed lockerTargetAddress,
        uint256 burntAmount,
        uint256 lockerFee,
        uint256 burningTime
    );

    event NewCollateralToken(address token, uint decimal);

    event MinterAdded(address indexed account);

    event MinterRemoved(address indexed account);

    event BurnerAdded(address indexed account);

    event BurnerRemoved(address indexed account);

    event NewLockerPercentageFee(
        uint256 oldLockerPercentageFee,
        uint256 newLockerPercentageFee
    );

    event NewReliabilityFactor(
        address lockerTargetAddress,
        uint oldReliabilityFactor,
        uint newReliabilityFactor
    );

    event NewPriceWithDiscountRatio(
        uint256 oldPriceWithDiscountRatio,
        uint256 newPriceWithDiscountRatio
    );

    event NewMinRequiredTSTLockedAmount(
        uint256 oldMinRequiredTSTLockedAmount,
        uint256 newMinRequiredTSTLockedAmount
    );

    event NewMinRequiredTNTLockedAmount(
        uint256 oldMinRequiredTNTLockedAmount,
        uint256 newMinRequiredTNTLockedAmount
    );

    event NewPriceOracle(address oldPriceOracle, address newPriceOracle);

    event NewBurnRouter(address oldBurnRouter, address newBurnRouter);

    event NewTST(address oldTST, address newTST);

    event NewTeleBTC(address oldTeleBTC, address newTeleBTC);

    event NewCollateralRatio(
        uint256 oldCollateralRatio,
        uint256 newCollateralRatio
    );

    event NewLiquidationRatio(
        uint256 oldLiquidationRatio,
        uint256 newLiquidationRatio
    );

    // Read-only functions

    function TeleportSystemToken() external view returns (address);

    function teleBTC() external view returns (address);

    function burnRouter() external view returns (address);

    function exchangeConnector() external view returns (address);

    function priceOracle() external view returns (address);

    function minRequiredTSTLockedAmount() external view returns (uint256);

    function minRequiredTNTLockedAmount() external view returns (uint256);

    function lockerPercentageFee() external view returns (uint256);

    function collateralRatio() external view returns (uint256);

    function liquidationRatio() external view returns (uint256);

    function priceWithDiscountRatio() external view returns (uint256);

    function totalNumberOfCandidates() external view returns (uint256);

    function totalNumberOfLockers() external view returns (uint256);

    function getLockerTargetAddress(
        bytes calldata _lockerLockingScript
    ) external view returns (address);

    function isLocker(
        bytes calldata _lockerLockingScript
    ) external view returns (bool);

    function isLockerActive(
        address _lockerTargetAddress
    ) external view returns (bool);

    function getLockerHealthFactor(
        address _lockerTargetAddress
    ) external view returns (uint256);

    function minters(address) external view returns (bool);

    function burners(address) external view returns (bool);

    // State-changing functions

    function pauseLocker() external;

    function unPauseLocker() external;

    function addCollateralToken(address _token, uint _decimal) external;

    function addMinter(address _account) external;

    function removeMinter(address _account) external;

    function addBurner(address _account) external;

    function removeBurner(address _account) external;

    function mint(
        bytes calldata _lockerLockingScript,
        address _receiver,
        uint256 _amount
    ) external returns (uint256);

    function burn(
        bytes calldata _lockerLockingScript,
        uint256 _amount
    ) external returns (uint256);

    function setTST(address _TST) external;

    function setLockerPercentageFee(uint256 _lockerPercentageFee) external;

    function setPriceWithDiscountRatio(
        uint256 _priceWithDiscountRatio
    ) external;

    function setMinRequiredTSTLockedAmount(
        uint256 _minRequiredTSTLockedAmount
    ) external;

    function setPriceOracle(address _priceOracle) external;

    function setBurnRouter(address _burnRouter) external;

    function setTeleBTC(address _teleBTC) external;

    function setLockerReliabilityFactor(
        address _lockerTargetAddress,
        uint _reliabilityFactor
    ) external;

    function setCollateralRatio(uint _collateralRatio) external;

    function setLiquidationRatio(uint256 _liquidationRatio) external;

    function liquidateLocker(
        address _lockerTargetAddress,
        uint256 _btcAmount
    ) external returns (bool);

    function addCollateral(
        address _lockerTargetAddress,
        uint256 _addingCollateralTokenAmount
    ) external payable returns (bool);

    function removeCollateral(
        uint256 _removingCollateralTokenAmount
    ) external payable returns (bool);

    function requestToBecomeLocker(
        bytes calldata _lockerLockingScript,
        address _collateralToken,
        uint _lockedTSTAmount,
        uint _lockedCollateralTokenAmount,
        ScriptTypes _lockerScriptType,
        bytes calldata _lockerRescueScript
    ) external payable returns (bool);

    function revokeRequest() external returns (bool);

    function addLocker(
        address _lockerTargetAddress,
        uint256 _lockerReliabilityFactor
    ) external returns (bool);

    function requestInactivation() external returns (bool);

    function requestActivation() external returns (bool);

    function selfRemoveLocker() external returns (bool);

    function slashIdleLocker(
        address _lockerTargetAddress,
        uint256 _rewardAmount,
        address _slasher,
        uint256 _amount,
        address _recipient
    ) external returns (bool);

    function slashThiefLocker(
        address _lockerTargetAddress,
        uint256 _rewardAmount,
        address _slasher,
        uint256 _amount
    ) external returns (bool);

    function buySlashedCollateralOfLocker(
        address _lockerTargetAddress,
        uint256 _collateralAmount
    ) external returns (bool);

    function getLockerCapacity(
        address _lockerTargetAddress
    ) external returns (uint256 theLockerCapacity);
}
","keccak256":"0x712c2a5b5f236fda538191b96be4b67834c7fdf3fa9e43800885f8a58af471f5","license":"MIT"},"contracts/routers/CcExchangeRouterLib.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <=0.8.4;

import "@teleportdao/btc-evm-bridge/contracts/relay/interfaces/IBitcoinRelay.sol";
import "@teleportdao/btc-evm-bridge/contracts/libraries/BitcoinHelper.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "../routers/interfaces/ICcExchangeRouter.sol";
import "./RequestParser.sol";

library CcExchangeRouterLib {
    /// @notice Parses and stores exchange request if it's valid
    function ccExchangeHelper(
        ICcExchangeRouter.TxAndProof memory _txAndProof,
        mapping(bytes32 => ICcExchangeRouter.ccExchangeRequest)
            storage ccExchangeRequests,
        mapping(bytes32 => ICcExchangeRouter.extendedCcExchangeRequest)
            storage extendedCcExchangeRequests,
        address _teleBTC,
        bytes memory _lockerLockingScript,
        address _relay
    ) external returns (bytes32) {
        // Finds tx id
        bytes32 txId = BitcoinHelper.calculateTxId(
            _txAndProof.version,
            _txAndProof.vin,
            _txAndProof.vout,
            _txAndProof.locktime
        );

        // Checks that the request has not been processed before
        require(
            !ccExchangeRequests[txId].isUsed,
            "ExchangeRouterLib: already used"
        );

        // Extracts value and OP_RETURN data from the request
        ICcExchangeRouter.ccExchangeRequest memory request;
        bytes memory arbitraryData;

        (request.inputAmount, arbitraryData) = BitcoinHelper
            .parseValueAndDataHavingLockingScriptSmallPayload(
                _txAndProof.vout,
                _lockerLockingScript
            );

        /*  
            Exchange requests structure:
            1) chainId, 2 byte: max 65535 chains
            2) appId, 1 byte: max 256 apps
            3) recipientAddress, 20 byte: EVM account
            4) networkFee, 3 byte
            5) SPEED, 1 byte: {0,1}
            6) thirdParty, 1 byte: max 256 third parties, default is 0 for no third party
            7) exchangeToken, 20 byte: token address
            8) outputAmount, 14 byte: min expected output amount. Assuming that the token supply
               is less than 10^15 and token decimal is 18 (> (10^18) * (10^18))
            9) bridgeFee, 3 byte: will be multiply by 10^11, 10^18 means 100%, so the minimum 
            amount of fee percentage is 10^-5%
            TOTAL = 65 BYTE
        */
        require(arbitraryData.length == 65, "ExchangeRouterLib: invalid len");
        require(request.inputAmount > 0, "ExchangeRouterLib: zero input");

        extendedCcExchangeRequests[txId].chainId = RequestParser.parseChainId(
            arbitraryData
        );
        extendedCcExchangeRequests[txId].bridgePercentageFee =
            uint(RequestParser.parseArossFeePercentage(arbitraryData)) *
            (10 ** 11);
        extendedCcExchangeRequests[txId].thirdParty = RequestParser
            .parseThirdPartyId(arbitraryData);

        request.appId = RequestParser.parseAppId(arbitraryData);
        address exchangeToken = RequestParser.parseExchangeToken(arbitraryData);
        request.outputAmount = RequestParser.parseExchangeOutputAmount(
            arbitraryData
        );
        request.isFixedToken = true; // Note: we assume input amount is fixed
        request.recipientAddress = RequestParser.parseRecipientAddress(
            arbitraryData
        );

        // Note: default exchange path is: [teleBTC, exchangeToken]
        request.path = new address[](2);
        request.path[0] = _teleBTC;
        request.path[1] = exchangeToken;

        // Finds Teleporter fee
        uint networkFee = RequestParser.parseNetworkFee(arbitraryData);

        require(
            networkFee <= request.inputAmount,
            "ExchangeRouterLib: wrong fee"
        );
        request.fee = networkFee;

        // Note: speed now determines using fillers to speed up filling request (speed = 1) or not
        request.speed = RequestParser.parseSpeed(arbitraryData);

        request.isUsed = true;

        // Saves request
        ccExchangeRequests[txId] = request;

        require(
            _isConfirmed(_txAndProof, _relay, txId),
            "ExchangeRouter: not finalized"
        );

        return txId;
    }

    /// @notice Verifies the signature of _msgHash
    /// @return _signer Address of message signer (if signature is valid)
    function _verifySig(
        bytes memory message,
        bytes32 r,
        bytes32 s,
        uint8 v
    ) public pure returns (address) {
        // Compute the message hash
        bytes32 messageHash = keccak256(message);

        // Prefix the message hash as per the Ethereum signing standard
        bytes32 ethSignedMessageHash = keccak256(
            abi.encodePacked("\x19Ethereum Signed Message:\n32", messageHash)
        );

        // Verify the message using ecrecover
        address signer = ecrecover(ethSignedMessageHash, v, r, s);
        require(signer != address(0), "PolygonConnectorLogic: Invalid sig");

        return signer;
    }

    /// @notice Checks inclusion of the transaction in the specified block
    /// @dev Calls the relay contract to check Merkle inclusion proof
    /// @param _relay Address of Relay contract
    /// @param _txId of the transaction
    /// @return True if the transaction was included in the block
    function _isConfirmed(
        ICcExchangeRouter.TxAndProof memory _txAndProof,
        address _relay,
        bytes32 _txId
    ) internal returns (bool) {
        // Finds fee amount
        uint feeAmount = _getFinalizedBlockHeaderFee(
            _relay,
            _txAndProof.blockNumber
        );
        require(msg.value >= feeAmount, "ExchangeRouterLib: low fee");

        // Calls relay contract
        bytes memory data = Address.functionCallWithValue(
            _relay,
            abi.encodeWithSignature(
                "checkTxProof(bytes32,uint256,bytes,uint256)",
                _txId,
                _txAndProof.blockNumber,
                _txAndProof.intermediateNodes,
                _txAndProof.index
            ),
            feeAmount
        );

        // Sends extra ETH back to msg.sender
        Address.sendValue(payable(msg.sender), msg.value - feeAmount);

        return abi.decode(data, (bool));
    }

    function _getFinalizedBlockHeaderFee(
        address _relay,
        uint _blockNumber
    ) private view returns (uint) {
        return IBitcoinRelay(_relay).getBlockHeaderFee(_blockNumber, 0);
    }

    /// @notice Parses and stores exchange request if it's valid for Solana requests
    function ccExchangeToSolanaHelper(
        ICcExchangeRouter.TxAndProof memory _txAndProof,
        mapping(bytes32 => ICcExchangeRouter.ccExchangeToSolanaRequest)
            storage ccExchangeToSolanaRequests,
        mapping(bytes32 => ICcExchangeRouter.extendedCcExchangeRequest)
            storage extendedCcExchangeRequests,
        address _teleBTC,
        bytes memory _lockerLockingScript,
        address _relay
    ) external returns (bytes32) {
        // Finds tx id
        bytes32 txId = BitcoinHelper.calculateTxId(
            _txAndProof.version,
            _txAndProof.vin,
            _txAndProof.vout,
            _txAndProof.locktime
        );

        // Checks that the request has not been processed before
        require(
            !ccExchangeToSolanaRequests[txId].isUsed,
            "ExchangeRouterLib: already used"
        );

        // Extracts value and OP_RETURN data from the request
        ICcExchangeRouter.ccExchangeToSolanaRequest memory request;
        bytes memory arbitraryData;

        (request.inputAmount, arbitraryData) = BitcoinHelper
            .parseValueAndDataHavingLockingScriptBigPayload(
                _txAndProof.vout,
                _lockerLockingScript
            );

        /*  
            Exchange requests structure:
            1) destChainId, 2 byte: max 65535 chains
            2) appId, 1 byte: max 256 apps
            3) recipientDestAddress, 32 byte: Solana address, or zero padded EVM address
            4) networkFee, 3 byte
            5) speed, 1 byte: {0,1}
            6) thirdPartyID, 1 byte: max 256 third parties, default is 0 for no third party
            7) destTokenTicker, 8 byte: token ticker
            8) minDestTokenAmount, 13 byte: minimum expected destination token amount (output amount)
            9) minIntermediaryTokenAmount, 13 byte: minimum intermediary token amount (slippage guard on Polygon)
            10) bridgePercentageFee, 3 byte: will be multiply by 10^11, 10^18 means 100%, so the minimum 
            amount of fee percentage is 10^-5%
            TOTAL = 77 BYTE
        */
        require(arbitraryData.length == 77, "ExchangeRouterLib: invalid len");
        require(request.inputAmount > 0, "ExchangeRouterLib: zero input");

        extendedCcExchangeRequests[txId].chainId = RequestParser.parseChainId(
            arbitraryData
        );
        extendedCcExchangeRequests[txId].bridgePercentageFee =
            uint(RequestParser.parseBridgeFeePercentage(arbitraryData)) *
            (10 ** 11);
        extendedCcExchangeRequests[txId].thirdParty = RequestParser
            .parseThirdPartyID(arbitraryData);

        request.appId = RequestParser.parseAppId(arbitraryData);
        bytes8 destTokenTicker = bytes8(RequestParser.parseDestTokenTicker(arbitraryData));
        request.outputAmount = RequestParser.parseMinDestTokenAmount(
            arbitraryData
        );

        request.minIntermediaryTokenAmount = RequestParser.parseMinIntermediaryTokenAmount(
            arbitraryData
        );
        request.isFixedToken = true; // Note: we assume input amount is fixed
        request.recipientAddress = RequestParser.parseRecipientDestAddress(
            arbitraryData
        );

        // Note: default exchange path is: [teleBTC, exchangeToken]
        request.tokenTickers = new bytes8[](2);
        request.tokenTickers[0] = bytes8(uint64(uint160(_teleBTC)));
        request.tokenTickers[1] = destTokenTicker;

        // Finds Teleporter fee
        uint networkFee = RequestParser.parseNetworkFeeNew(arbitraryData);

        require(
            networkFee <= request.inputAmount,
            "ExchangeRouterLib: wrong fee"
        );
        request.fee = networkFee;

        // Note: speed now determines using fillers to speed up filling request (speed = 1) or not
        request.speed = RequestParser.parseSpeedNew(arbitraryData);

        request.isUsed = true;

        // Saves request
        ccExchangeToSolanaRequests[txId] = request;

        require(
            _isConfirmed(_txAndProof, _relay, txId),
            "ExchangeRouter: not finalized"
        );

        return txId;
    }
}
","keccak256":"0xb89389fc05c654a76ef4427dcbc81ba1a1e72bb9ddc934f130723df1d6e45249","license":"MIT"},"contracts/routers/CcExchangeToSolanaRouterLib.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <=0.8.4;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "../routers/interfaces/ICcExchangeRouter.sol";
import "../dex_connectors/interfaces/IDexConnector.sol";
import "../erc20/interfaces/ITeleBTC.sol";
import "../erc20/WETH.sol";
import "../lockersManager/interfaces/ILockersManager.sol";
import "../routers/CcExchangeRouterLib.sol";

library CcExchangeToSolanaRouterLib {
    using Address for address;

    // Constants
    uint256 constant MAX_BRIDGE_FEE = 10 ** 18;

    // Events
    event NewWrapAndSwapV2(
        address lockerTargetAddress,
        bytes32 indexed user,
        bytes32[2] inputAndOutputToken,
        uint[2] inputAndOutputAmount,
        uint indexed speed,
        address indexed teleporter,
        bytes32 bitcoinTxId,
        uint appId,
        uint thirdPartyId,
        uint[5] fees,
        uint destinationChainId
    );

    event FailedWrapAndSwapV2(
        address lockerTargetAddress,
        bytes32 indexed recipientAddress,
        bytes32[2] inputAndOutputToken,
        uint[2] inputAndOutputAmount,
        uint indexed speed,
        address indexed teleporter,
        bytes32 bitcoinTxId,
        uint appId,
        uint thirdPartyId,
        uint[5] fees,
        uint destinationChainId
    );

    /// @notice Swap TeleBTC for the output token
    function swapToSolana(
        ICcExchangeRouter.swapToSolanaArguments memory swapArguments,
        ICcExchangeRouter.SwapToSolanaData memory _swapToSolanaData
    ) external returns (bool result, uint256[] memory amounts) {
        // Give allowance to exchange connector for swapping
        ITeleBTC(_swapToSolanaData.teleBTC).approve(
            swapArguments._exchangeConnector,
            swapArguments._extendedCcExchangeRequest.remainedInputAmount
        );

        // Check if the provided path is valid
        require(
            address(uint160(uint256(swapArguments._path[0]))) == _swapToSolanaData.teleBTC &&
                swapArguments._path[swapArguments._path.length - 1] ==
                swapArguments._ccExchangeToSolanaRequest.path[
                    swapArguments._ccExchangeToSolanaRequest.path.length - 1
                ],
            "CcExchangeToSolanaRouter: invalid path"
        );

        // Swap teleBTC for the output token
        // Swapped token is sent to the contract
        address[] memory _path = new address[](2);
        _path[0] = address(uint160(uint256(swapArguments._path[0])));
        _path[1] = address(uint160(uint256(swapArguments._path[swapArguments._path.length - 1])));

        uint256 outputAmount = swapArguments._ccExchangeToSolanaRequest.outputAmount;
        uint256 bridgePercentageFee = swapArguments._extendedCcExchangeRequest.bridgePercentageFee;
        uint256 minAmountOut = (outputAmount * MAX_BRIDGE_FEE) / (MAX_BRIDGE_FEE - bridgePercentageFee);

        (result, amounts) = IDexConnector(swapArguments._exchangeConnector)
            .swap(
                swapArguments._extendedCcExchangeRequest.remainedInputAmount,
                minAmountOut,
                _path,
                address(this),
                block.timestamp,
                true
            );

        if (result) {
            _handleSuccessfulSwap(swapArguments, amounts, _swapToSolanaData);
        } else {
            _handleFailedSwap(swapArguments, _swapToSolanaData);
        }

        return (result, amounts);
    }

    /// @notice Handle successful swap logic
    function _handleSuccessfulSwap(
        ICcExchangeRouter.swapToSolanaArguments memory swapArguments,
        uint256[] memory amounts,
        ICcExchangeRouter.SwapToSolanaData memory _swapToSolanaData
    ) private {
        // Send tokens to user if on current chain
        if (swapArguments.destinationChainId == block.chainid) {
            address outputToken = address(uint160(uint256(swapArguments._path[swapArguments._path.length - 1])));
            uint256 outputAmount = amounts[amounts.length - 1];
            address recipient = address(uint160(uint256(swapArguments._ccExchangeToSolanaRequest.recipientAddress)));

            if (outputToken != _swapToSolanaData.wrappedNativeToken) {
                ITeleBTC(outputToken).transfer(recipient, outputAmount);
            } else {
                WETH(_swapToSolanaData.wrappedNativeToken).withdraw(outputAmount);
                Address.sendValue(payable(recipient), outputAmount);
            }
        }

        // Emit success event
        uint256 bridgeFee = (amounts[amounts.length - 1] * swapArguments._extendedCcExchangeRequest.bridgePercentageFee) / MAX_BRIDGE_FEE;
        uint256[5] memory fees = [
            swapArguments._ccExchangeToSolanaRequest.fee,
            swapArguments._extendedCcExchangeRequest.lockerFee,
            swapArguments._extendedCcExchangeRequest.protocolFee,
            swapArguments._extendedCcExchangeRequest.thirdPartyFee,
            bridgeFee
        ];

        emit NewWrapAndSwapV2(
            ILockersManager(_swapToSolanaData.lockers).getLockerTargetAddress(swapArguments._lockerLockingScript),
            swapArguments._ccExchangeToSolanaRequest.recipientAddress,
            [bytes32(uint256(uint160(_swapToSolanaData.teleBTC))), swapArguments._path[swapArguments._path.length - 1]],
            [amounts[0], amounts[amounts.length - 1] - bridgeFee],
            swapArguments._ccExchangeToSolanaRequest.speed,
            _swapToSolanaData.teleporter,
            swapArguments._txId,
            swapArguments._ccExchangeToSolanaRequest.appId,
            swapArguments._extendedCcExchangeRequest.thirdParty,
            fees,
            swapArguments.destinationChainId
        );
    }

    /// @notice Handle failed swap logic
    function _handleFailedSwap(
        ICcExchangeRouter.swapToSolanaArguments memory swapArguments,
        ICcExchangeRouter.SwapToSolanaData memory _swapToSolanaData
    ) private {
        uint256[5] memory fees = [uint256(0), uint256(0), uint256(0), uint256(0), uint256(0)];

        emit FailedWrapAndSwapV2(
            ILockersManager(_swapToSolanaData.lockers).getLockerTargetAddress(swapArguments._lockerLockingScript),
            swapArguments._ccExchangeToSolanaRequest.recipientAddress,
            [bytes32(uint256(uint160(_swapToSolanaData.teleBTC))), swapArguments._path[swapArguments._path.length - 1]],
            [swapArguments._extendedCcExchangeRequest.remainedInputAmount, 0],
            swapArguments._ccExchangeToSolanaRequest.speed,
            _swapToSolanaData.teleporter,
            swapArguments._txId,
            swapArguments._ccExchangeToSolanaRequest.appId,
            swapArguments._extendedCcExchangeRequest.thirdParty,
            fees,
            swapArguments.destinationChainId
        );
    }
}
","keccak256":"0x7d49e1ab87c4ad7e50cdfa7171314fb8c20857683e3accb452e94b82ebb40df7","license":"MIT"},"contracts/routers/RequestParser.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <=0.8.4;

/// @notice Library for parsing cc transfer and cc exchange requests
library RequestParser {
    /// @notice Returns chain id of the request
    /// @param _arbitraryData Data written in Bitcoin tx
    function parseChainId(
        bytes memory _arbitraryData
    ) internal pure returns (uint16 parsedValue) {
        bytes memory slicedBytes = sliceBytes(_arbitraryData, 0, 1);
        assembly {
            parsedValue := mload(add(slicedBytes, 2))
        }
    }

    /// @notice Returns app id of the request
    /// @dev Determines the app that request belongs to (e.g. cc transfer app id is 0)
    function parseAppId(
        bytes memory _arbitraryData
    ) internal pure returns (uint8 parsedValue) {
        bytes memory slicedBytes = sliceBytes(_arbitraryData, 2, 2);
        assembly {
            parsedValue := mload(add(slicedBytes, 1))
        }
    }

    /// @notice Returns recipient address
    /// @dev Minted TeleBTC or exchanged tokens will be sent to this address
    function parseRecipientAddress(
        bytes memory _arbitraryData
    ) internal pure returns (address parsedValue) {
        bytes memory slicedBytes = sliceBytes(_arbitraryData, 3, 22);
        assembly {
            parsedValue := mload(add(slicedBytes, 20))
        }
    }

    /// @notice Returns network fee
    /// @dev This fee goes to Teleporter who submitted the request
    function parseNetworkFee(
        bytes memory _arbitraryData
    ) internal pure returns (uint24 parsedValue) {
        bytes memory slicedBytes = sliceBytes(_arbitraryData, 23, 25);
        assembly {
            parsedValue := mload(add(slicedBytes, 3))
        }
    }

    /// @notice Determines type of the request
    /// @dev 0 for normal requests, 1 for fixed-rate requests
    function parseSpeed(
        bytes memory _arbitraryData
    ) internal pure returns (uint8 parsedValue) {
        bytes memory slicedBytes = sliceBytes(_arbitraryData, 26, 26);
        assembly {
            parsedValue := mload(add(slicedBytes, 1))
        }
    }

    /// @notice Returns id of third party
    /// @dev 0 for no third party
    function parseThirdPartyId(
        bytes memory _arbitraryData
    ) internal pure returns (uint8 parsedValue) {
        bytes memory slicedBytes = sliceBytes(_arbitraryData, 27, 27);
        assembly {
            parsedValue := mload(add(slicedBytes, 1))
        }
    }

    /// @notice Returns address of exchange token
    /// @dev Minted TeleBTC will be exchanged for this token
    function parseExchangeToken(
        bytes memory _arbitraryData
    ) internal pure returns (address parsedValue) {
        bytes memory slicedBytes = sliceBytes(_arbitraryData, 28, 47);
        assembly {
            parsedValue := mload(add(slicedBytes, 20))
        }
    }

    /// @notice Returns min expected output (exchange) amount
    function parseExchangeOutputAmount(
        bytes memory _arbitraryData
    ) internal pure returns (uint112 parsedValue) {
        bytes memory slicedBytes = sliceBytes(_arbitraryData, 48, 61);
        assembly {
            parsedValue := mload(add(slicedBytes, 14))
        }
    }

    /// @notice Returns across percentage fee
    /// @dev This fee goes to across relayers
    function parseArossFeePercentage(
        bytes memory _arbitraryData
    ) internal pure returns (uint24 parsedValue) {
        bytes memory slicedBytes = sliceBytes(_arbitraryData, 62, 64);
        assembly {
            parsedValue := mload(add(slicedBytes, 3))
        }
    }

    /// @notice Returns the sliced bytes
    /// @param _data Slicing data
    /// @param _start index of slicing
    /// @param _end index of slicing
    function sliceBytes(
        bytes memory _data,
        uint _start,
        uint _end
    ) internal pure returns (bytes memory _result) {
        bytes1 temp;
        for (uint i = _start; i < _end + 1; i++) {
            temp = _data[i];
            _result = abi.encodePacked(_result, temp);
        }
    }

    /// @notice Returns recipient address
    /// @dev Minted TeleBTC or exchanged tokens will be sent to this address
    function parseRecipientDestAddress(
        bytes memory _arbitraryData
    ) internal pure returns (bytes32 parsedValue) {
        bytes memory slicedBytes = sliceBytes(_arbitraryData, 3, 34);
        assembly {
            parsedValue := mload(add(slicedBytes, 32))
        }
    }

    /// @notice Returns network fee
    /// @dev This fee goes to Teleporter who submitted the request
    function parseNetworkFeeNew(
        bytes memory _arbitraryData
    ) internal pure returns (uint24 parsedValue) {
        bytes memory slicedBytes = sliceBytes(_arbitraryData, 35, 37);
        assembly {
            parsedValue := mload(add(slicedBytes, 3))
        }
    }

    /// @notice Determines type of the request
    /// @dev 0 for normal requests, 1 for fixed-rate requests
    function parseSpeedNew(
        bytes memory _arbitraryData
    ) internal pure returns (uint8 parsedValue) {
        bytes memory slicedBytes = sliceBytes(_arbitraryData, 38, 38);
        assembly {
            parsedValue := mload(add(slicedBytes, 1))
        }
    }

    /// @notice Returns id of third party
    /// @dev 0 for no third party
    function parseThirdPartyID(
        bytes memory _arbitraryData
    ) internal pure returns (uint8 parsedValue) {
        bytes memory slicedBytes = sliceBytes(_arbitraryData, 39, 39);
        assembly {
            parsedValue := mload(add(slicedBytes, 1))
        }
    }

    /// @notice Returns token ticker of the destination token
    /// @dev Minted TeleBTC will be exchanged for this token
    function parseDestTokenTicker(
        bytes memory _arbitraryData
    ) internal pure returns (uint64 parsedValue) {
        bytes memory slicedBytes = sliceBytes(_arbitraryData, 40, 47);
        assembly {
            parsedValue := mload(add(slicedBytes, 8))
        }
    }

    /// @notice Returns min expected output (exchange) amount
    function parseMinDestTokenAmount(
        bytes memory _arbitraryData
    ) internal pure returns (uint104 parsedValue) {
        bytes memory slicedBytes = sliceBytes(_arbitraryData, 48, 60);
        assembly {
            parsedValue := mload(add(slicedBytes, 13))
        }
    }

    /// @notice Returns min expected output (exchange) amount
    function parseMinIntermediaryTokenAmount(
        bytes memory _arbitraryData
    ) internal pure returns (uint104 parsedValue) {
        bytes memory slicedBytes = sliceBytes(_arbitraryData, 61, 73);
        assembly {
            parsedValue := mload(add(slicedBytes, 13))
        }
    }

    /// @notice Returns across percentage fee
    /// @dev This fee goes to across relayers
    function parseBridgeFeePercentage(
        bytes memory _arbitraryData
    ) internal pure returns (uint24 parsedValue) {
        bytes memory slicedBytes = sliceBytes(_arbitraryData, 74, 76);
        assembly {
            parsedValue := mload(add(slicedBytes, 3))
        }
    }
}
","keccak256":"0x0d6768ae741ce638c04c3d969531cdfea2b6a930a65efc0a13e587953c8a535f","license":"MIT"},"contracts/routers/interfaces/ICcExchangeRouter.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <=0.8.4;

interface ICcExchangeRouter {

    // Structures

    struct chainIdStruct {
        uint middleChain;
        uint destinationChain;
    }

    /// @notice Structure for recording cross-chain exchange requests
    /// @param appId that user wants to use (which DEX)
    /// @param inputAmount Amount of locked BTC on source chain
    /// @param outputAmount Amount of output token
    /// @param isFixedToken True if amount of input token is fixed
    /// @param recipientAddress Address of exchange recipient
    /// @param fee Amount of fee that is paid to Teleporter (for tx, relayer and teleporter fees)
    /// @param isUsed True if tx has been submitted before
    /// @param path Exchange path from input token to output token
    /// @param deadline for exchanging tokens (not used anymore)
    /// @param speed of the request (normal or instant)
    struct ccExchangeRequest {
        uint appId;
        uint inputAmount;
        uint outputAmount;
        bool isFixedToken;
        address recipientAddress;
        uint fee;
        bool isUsed;
        address[] path;
        uint deadline;
        uint speed;
        uint destinationChain;
    }

    /// @notice Structure for recording cross-chain exchange requests
    /// @param isRequestCompleted True if BTC to ETH exchange is processed successfully
    /// @param remainedInputAmount Amount of obtained TELEBTC on target chain
    /// @param bridgePercentageFee percentage of fee we have to give to across relayers to fill our request
    struct extendedCcExchangeRequest {
        uint chainId;
        bool isRequestCompleted;
        uint remainedInputAmount;
        uint bridgePercentageFee;
        uint thirdParty;
        uint protocolFee;
        uint thirdPartyFee;
        uint lockerFee;
    }

    /// @notice Structure for passing tx and its inclusion proof
    /// @param version of the transaction containing the user request
    /// @param vin Inputs of the transaction containing the user request
    /// @param vout Outputs of the transaction containing the user request
    /// @param locktime of the transaction containing the user request
    /// @param blockNumber Height of the block containing the user request
    /// @param intermediateNodes Merkle inclusion proof for transaction containing the user request
    /// @param index of transaction containing the user request in the block
    struct TxAndProof {
        bytes4 version;
        bytes vin;
        bytes vout;
        bytes4 locktime;
        uint256 blockNumber;
        bytes intermediateNodes;
        uint index;
    }

    /// @notice Structure for storing filling requests
    /// @param startingTime First attemp to fill the request
    /// @param reqToken Requested exchange token
    /// @param lastUsedIdx Last used filler index
    /// @param remainingAmountOfLastFill Amount of unused tokens of last filler
    /// @param isWithdrawnLastFill True if last used filler has withdrawn unused tokens
    struct FillData {
        uint startingTime;
        address reqToken;
        uint lastUsedIdx;
        uint remainingAmountOfLastFill;
        bool isWithdrawnLastFill;
    }

    /// @notice Structure for storing fillers of a request
    /// @param index of filler between fillers
    /// @param token that filler used to fill
    /// @param amount that filler sent to fill
    struct FillerData {
        uint index;
        address token;
        uint amount;
    }

    /// @notice Structure for storing fillings
    /// @param prefixSum Cumulative sum of fillings
    /// @param currentIndex Next filler index
    struct PrefixFillSum {
        uint[] prefixSum;
        uint currentIndex;
    }

    /// @notice Structure for passing arguments to swap function
    struct swapArguments {
        uint destinationChainId;
        bytes _lockerLockingScript;
        ccExchangeRequest _ccExchangeRequest;
        extendedCcExchangeRequest _extendedCcExchangeRequest;
        bytes32 _txId;
        address[] _path;
        address _exchangeConnector;
    }

    // Events

    event AcrossUpdated(
        address oldAcross,
        address newAcross
    );

    event BurnRouterUpdated(
        address oldBurnRouter,
        address newBurnRouter
    );

    /// @notice Emits when a new filler fills a request
    /// @param filler Address of filler
    /// @param user Address of user
    /// @param lockerTargetAddress Address of Locker
    /// @param bitcoinTxId The transaction ID of request on Bitcoin 
    /// @param inputAndOutputToken [inputToken, outputToken]
    /// @param userRequestedAmount that user requested
    /// @param finalAmount that user received
    /// @param destinationChainId chain id of destination 
    /// @param bridgePercentageFee percentage of fee we have to give to across relayers to fill our request
    event RequestFilled(
        address filler,
        address user,
        address lockerTargetAddress,
        bytes32 bitcoinTxId,
        address[2] inputAndOutputToken,
        uint fillAmount,
        uint finalAmount,
        uint userRequestedAmount,
        uint destinationChainId,
        uint bridgePercentageFee
    );

    event FillerRefunded(
        address filler,
        bytes32 bitcoinTxId,
        uint amount
    );

    /// @notice Emits when a cc exchange request gets done
    /// @param lockerTargetAddress Address of Locker
    /// @param user Exchange recipient address
    /// @param inputAndOutputToken [inputToken, outputToken]
    /// @param inputAndOutputAmount [inputAmount, outputAmount]
    /// @param speed Speed of the request (normal or instant)
    /// @param teleporter Address of teleporter who submitted the request
    /// @param bitcoinTxId The transaction ID of request on Bitcoin 
    /// @param appId Assigned application id to exchange
    /// @param thirdPartyId Id of third party
    /// @param fees [network fee, locker fee, protocol fee, third party fee, bridge fee]
    /// @param destinationChainId chain id of destination 
    event NewWrapAndSwap(
        address lockerTargetAddress,
        address indexed user,
        address[2] inputAndOutputToken,
        uint[2] inputAndOutputAmount,
        uint indexed speed,
        address indexed teleporter,
        bytes32 bitcoinTxId,
        uint appId,
        uint thirdPartyId,
        uint[5] fees,
        uint destinationChainId
    );

    /// @notice Emits when a cc exchange request fails
    /// @dev We mint teleBTC and send it to the user
    /// @param lockerTargetAddress Address of Locker
    /// @param recipientAddress Exchange recipient address
    /// @param inputAndOutputToken [inputToken, outputToken]
    /// @param inputAndOutputAmount [inputAmount, outputAmount]
    /// @param speed Speed of the request (normal or instant)
    /// @param teleporter Address of teleporter who submitted the request
    /// @param bitcoinTxId The transaction ID of request on Bitcoin 
    /// @param appId Assigned application id to exchange
    /// @param thirdPartyId Id of third party
    /// @param fees [network fee, locker fee, protocol fee, third party fee, bridge fee]   
    /// @param destinationChainId chain id of destination 
    event FailedWrapAndSwap(
        address lockerTargetAddress,
        address indexed recipientAddress,
        address[2] inputAndOutputToken,
        uint[2] inputAndOutputAmount,
        uint indexed speed,
        address indexed teleporter,
        bytes32 bitcoinTxId,
        uint appId,
        uint thirdPartyId,
        uint[5] fees,
        uint destinationChainId
    );

    /// @notice Emits when a failed request is refunded
    event RefundProcessed(
        bytes32 indexed txId,
        address indexed refundedBy,
        uint256 failedRequestAmount,
        uint256 refundAmount,   
        bytes userScript,
        uint8 scriptType,
        address lockerTargetAddress,
        uint256 burnRequestCounter
    );

    /// @notice Emits when appId for an exchange connector is set
    /// @param appId Assigned application id to exchange
    /// @param exchangeConnector Address of exchange connector contract
    event SetExchangeConnector(
        uint appId,
        address exchangeConnector
    );

    /// @notice Emit when relay contract updated
    event NewRelay(
        address oldRelay, 
        address newRelay
    );

    /// @notice Emit when lockers contract updated
    event NewLockers(
        address oldLockers, 
        address newLockers
    );

    /// @notice Emit when telebtc contract updated
    event NewTeleBTC(
        address oldTeleBTC, 
        address newTeleBTC
    );

    /// @notice Emit when protocol fee updated
    event NewProtocolPercentageFee(
        uint oldProtocolPercentageFee, 
        uint newProtocolPercentageFee
    );

    /// @notice Emit when treasury address updated
    event NewTreasury(
        address oldTreasury, 
        address newTreasury
    );

    /// @notice Emit when third party address updated
	event NewThirdPartyAddress(
		uint thirdPartyId,
		address oldThirdPartyAddress, 
		address newThirdPartyAddress
	);

	/// @notice Emit when third party fee updated
	event NewThirdPartyFee(
		uint thirdPartyId,
		uint oldThirdPartyFee, 
		uint newThirdPartyFee
	);

    /// @notice Emit when new chain id mapping added
    event NewChainIdMapping(
        uint destinationChain,
        uint mappedChainId
    );

    /// @notice Emits when wrapped native token addr updated
    event NewWrappedNativeToken(
        address oldWrappedNativeToken,
        address newWrappedNativeToken
    );

    // Read-only functions

    function isRequestUsed(bytes32 _txId) external view returns (bool);

    // Read-only functions

    function startingBlockNumber() external view returns (uint);

    function protocolPercentageFee() external view returns (uint);

    function chainId() external view returns (uint);

    function relay() external view returns (address);

    function lockers() external view returns (address);

    function teleBTC() external view returns (address);

    function exchangeConnector(uint _appId) external view returns (address);

    function treasury() external view returns (address);

    function isTokenSupported(uint _chainId, address _exchangeToken) external view returns (bool);

    function isChainSupported(uint _chainId) external view returns (bool);

    function across() external view returns (address);

    function burnRouter() external view returns (address);

    // State-changing functions

    function setStartingBlockNumber(uint _startingBlockNumber) external;

    function setRelay(address _relay) external;

    function setTeleporter(address _teleporter, bool _isTeleporter) external;

    function setLockers(address _lockers) external;

    function setTeleBTC(address _teleBTC) external;

    function setExchangeConnector(uint _appId, address _exchangeConnector) external;

	function setTreasury(address _treasury) external;

	function setProtocolPercentageFee(uint _protocolPercentageFee) external;

    function setLockerPercentageFee(uint _lockerPercentageFee) external;

    function setAcross(address _across) external;

    function setBurnRouter(address _burnRouter) external;

    function setThirdPartyAddress(uint _thirdPartyId, address _thirdPartyAddress) external;

	function setThirdPartyFee(uint _thirdPartyId, uint _thirdPartyFee) external;

    function setWrappedNativeToken(address _wrappedNativeToken) external;

    function setChainIdMapping(uint _destinationChain, uint _mappedId) external;

    function setRewardDistributor(address _rewardDistributor) external;

    function setBridgeTokenMapping(
        address _sourceToken,
        uint256 _destinationChainId,
        address _destinationToken
    ) external;

    function wrapAndSwap(
        TxAndProof memory _txAndProof,
        bytes calldata _lockerLockingScript,
        address[] memory _path
    ) external payable returns(bool);

    function fillTx(
        bytes32 _txId,
        address _recipient,
        address _token,
        uint _fillAmount,
        uint _userRequestedAmount,
        uint _destinationChainId,
        uint _bridgePercentageFee,
        bytes memory _lockerLockingScript
    ) external payable;

    function refundByOwnerOrAdmin(
        bytes32 _txId,
        uint8 _scriptType,
        bytes memory _userScript,
        bytes calldata _lockerLockingScript
    ) external;

    /// @notice Structure for recording cross-chain exchange requests
    /// @param appId that user wants to use (which DEX)
    /// @param inputAmount Amount of locked BTC on source chain
    /// @param outputAmount Amount of output token
    /// @param isFixedToken True if amount of input token is fixed
    /// @param recipientAddress Address of exchange recipient: Solana address, or zero padded EVM address
    /// @param fee Amount of fee that is paid to Teleporter (for tx, relayer and teleporter fees)
    /// @param isUsed True if tx has been submitted before
    /// @param path Exchange path from input token to output token, token tickers
    /// @param deadline for exchanging tokens (not used anymore)
    /// @param speed of the request (normal or instant)
    struct ccExchangeToSolanaRequest {
        uint appId;
        uint inputAmount;
        uint outputAmount; // min expected output amount
        uint minIntermediaryTokenAmount;
        bool isFixedToken;
        bytes32 recipientAddress;
        uint fee;
        bool isUsed;
        bytes8[] tokenTickers;
        bytes32[] path;
        uint deadline;
        uint speed;
        uint destinationChain;
    }

    /// @notice Structure for passing arguments to swap function
    struct swapToSolanaArguments {
        uint destinationChainId;
        bytes _lockerLockingScript;
        ccExchangeToSolanaRequest _ccExchangeToSolanaRequest;
        extendedCcExchangeRequest _extendedCcExchangeRequest;
        bytes32 _txId;
        bytes32[] _path;
        address _exchangeConnector;
    }

    struct SwapToSolanaData {
        address teleBTC;
        address wrappedNativeToken;
        uint256 currentChainId;
        address lockers;
        address teleporter;
    }

    /// @notice Emits when a cc exchange request gets done
    /// @param lockerTargetAddress Address of Locker
    /// @param user Exchange recipient address
    /// @param inputAndOutputToken [inputToken, outputToken]
    /// @param inputAndOutputAmount [inputAmount, outputAmount]
    /// @param speed Speed of the request (normal or instant)
    /// @param teleporter Address of teleporter who submitted the request
    /// @param bitcoinTxId The transaction ID of request on Bitcoin 
    /// @param appId Assigned application id to exchange
    /// @param thirdPartyId Id of third party
    /// @param fees [network fee, locker fee, protocol fee, third party fee, bridge fee]
    /// @param destinationChainId chain id of destination 
    event NewWrapAndSwapV2(
        address lockerTargetAddress,
        bytes32 indexed user,
        bytes32[2] inputAndOutputToken,
        uint[2] inputAndOutputAmount,
        uint indexed speed,
        address indexed teleporter,
        bytes32 bitcoinTxId,
        uint appId,
        uint thirdPartyId,
        uint[5] fees,
        uint destinationChainId
    );

    /// @notice Emits when a cc exchange request fails
    /// @dev We mint teleBTC and send it to the user
    /// @param lockerTargetAddress Address of Locker
    /// @param recipientAddress Exchange recipient address
    /// @param inputAndOutputToken [inputToken, outputToken]
    /// @param inputAndOutputAmount [inputAmount, outputAmount]
    /// @param speed Speed of the request (normal or instant)
    /// @param teleporter Address of teleporter who submitted the request
    /// @param bitcoinTxId The transaction ID of request on Bitcoin 
    /// @param appId Assigned application id to exchange
    /// @param thirdPartyId Id of third party
    /// @param fees [network fee, locker fee, protocol fee, third party fee, bridge fee]   
    /// @param destinationChainId chain id of destination 
    event FailedWrapAndSwapV2(
        address lockerTargetAddress,
        bytes32 indexed recipientAddress,
        bytes32[2] inputAndOutputToken,
        uint[2] inputAndOutputAmount,
        uint indexed speed,
        address indexed teleporter,
        bytes32 bitcoinTxId,
        uint appId,
        uint thirdPartyId,
        uint[5] fees,
        uint destinationChainId
    );

    function setBridgeTokenTickerMapping(
        bytes8 _tokenTicker,
        uint256 _destinationChainId,
        bytes32 _destinationToken
    ) external;

    function wrapAndSwapV2(
        TxAndProof memory _txAndProof,
        bytes calldata _lockerLockingScript,
        address[] memory _path
    ) external payable returns(bool);
}","keccak256":"0x23fc844cced22efdd30ccba6cd8c2cdaf026dc61e8edb2d054bfcc286c8a5097","license":"MIT"}},"version":1}
